	public class RingBuffer<T> : IEnumerable<T>, IEnumerable, ICollection<T>, ICollection
	{
		protected int head;

		protected int tail;

		protected int size;

		protected T[] buffer;

		private bool allowOverflow;

		public bool AllowOverflow => allowOverflow;

		public int Capacity => buffer.Length;

		public int Size => size;

		public int Count => size;

		public bool IsReadOnly => false;

		public object SyncRoot => this;

		public bool IsSynchronized => false;

		public T Get()
		{
			if (size == 0)
			{
				throw new InvalidOperationException("Buffer is empty.");
			}
			T result = buffer[head];
			head = (head + 1) % Capacity;
			size--;
			return result;
		}

		public void Put(T item)
		{
			if (tail == head && size != 0)
			{
				if (!allowOverflow)
				{
					throw new InvalidOperationException("The RingBuffer is full");
				}
				addToBuffer(item, overflow: true);
			}
			else
			{
				addToBuffer(item, overflow: false);
			}
		}

		public void Put(T[] item)
		{
			foreach (T item2 in item)
			{
				Put(item2);
			}
		}

		protected void addToBuffer(T toAdd, bool overflow)
		{
			if (overflow)
			{
				head = (head + 1) % Capacity;
			}
			else
			{
				size++;
			}
			buffer[tail] = toAdd;
			tail = (tail + 1) % Capacity;
		}

		public RingBuffer()
			: this(4)
		{
		}

		public RingBuffer(int capacity)
			: this(capacity, overflow: false)
		{
		}

		public RingBuffer(int capacity, bool overflow)
		{
			buffer = new T[capacity];
			allowOverflow = overflow;
		}

		public IEnumerator<T> GetEnumerator()
		{
			int _index = head;
			int i = 0;
			while (i < size)
			{
				yield return buffer[_index];
				i++;
				_index = (_index + 1) % Capacity;
			}
		}

		IEnumerator<T> IEnumerable<T>.GetEnumerator()
		{
			return GetEnumerator();
		}

		IEnumerator IEnumerable.GetEnumerator()
		{
			return GetEnumerator();
		}

		public void Add(T item)
		{
			Put(item);
		}

		public bool Contains(T item)
		{
			EqualityComparer<T> @default = EqualityComparer<T>.Default;
			int num = head;
			int num2 = 0;
			while (num2 < size)
			{
				if (@default.Equals(item, buffer[num]))
				{
					return true;
				}
				num2++;
				num = (num + 1) % Capacity;
			}
			return false;
		}

		public void Clear()
		{
			for (int i = 0; i < Capacity; i++)
			{
				buffer[i] = default(T);
			}
			head = 0;
			tail = 0;
			size = 0;
		}

		public void CopyTo(T[] array, int arrayIndex)
		{
			int num = head;
			int num2 = 0;
			while (num2 < size)
			{
				array[arrayIndex] = buffer[num];
				num2++;
				arrayIndex++;
				num = (num + 1) % Capacity;
			}
		}

		public bool Remove(T item)
		{
			int num = head;
			int num2 = 0;
			bool flag = false;
			EqualityComparer<T> @default = EqualityComparer<T>.Default;
			int num3 = 0;
			while (num3 < size)
			{
				if (@default.Equals(item, buffer[num]))
				{
					num2 = num;
					flag = true;
					break;
				}
				num3++;
				num = (num + 1) % Capacity;
			}
			if (flag)
			{
				T[] array = new T[size - 1];
				num = head;
				bool flag2 = false;
				int num4 = 0;
				while (num4 < size - 1)
				{
					if (num == num2)
					{
						flag2 = true;
					}
					if (flag2)
					{
						array[num] = buffer[(num + 1) % Capacity];
					}
					else
					{
						array[num] = buffer[num];
					}
					num4++;
					num = (num + 1) % Capacity;
				}
				size--;
				buffer = array;
				return true;
			}
			return false;
		}

		void ICollection.CopyTo(Array array, int index)
		{
			CopyTo((T[])array, index);
		}
	}
