			string text = this.text.text;
			if (text.Length > 400)
			{
				text = text.Substring(text.Length - 50);
			}
			text += "\r\n \r\n";
			text += logMessage;
			this.text.text = text;
		}

		public bool DebugAssert(bool condition, string message)
		{
			if (!condition)
			{
				UpdateLog(message);
				return false;
			}
			return true;
		}
	}
	public class PermissionHelper
