		private void SetupAudio(AudioSource aud, string clipName)
		{
			int capacity = 32000;
			_audioBuffer = new RingBuffer<float>(capacity, overflow: true);
			_audioClip = AudioClip.Create(clipName, 320, 1, 32000, stream: true, OnAudioRead);
			aud.clip = _audioClip;
			aud.loop = true;
			aud.Play();
