		private void OnAudioRead(float[] data)
		{
			for (int i = 0; i < data.Length; i++)
			{
				lock (_audioBuffer)
				{
					if (_audioBuffer.Count > 0)
					{
						data[i] = _audioBuffer.Get();
						_readCount++;
					}
				}
			}
			UnityEngine.Debug.LogFormat("buffer length remains: {0}", _writeCount - _readCount);
		}

