			{
				text = (string)jsonData["messageSender"];
			}
			string text2 = "";
			if (msg.Contains("messageAdr"))
			{
				text2 = (string)jsonData["messageAdr"];
				if (text2.Substring(0, 3) == "+82")
				{
					text2 = "0" + text2.Remove(0, 3);
					text2 = TransformNumber(text2);
