				else
				{
					msgtoast.transform.Find("number").transform.localPosition = new Vector3(60f, 0f, 0f);
				}
				StartCoroutine(ToastManager(2, state: true, 0f));
				StartCoroutine(ToastManager(2, state: false, 5f));
