			protected Quaternion Rotation
			{
				get
				{
					if (Targets == null || Targets.Length == 0 || Target == null)
					{
						return Quaternion.identity;
					}
					if (RuntimeTools.PivotRotation != 0)
					{
						return Quaternion.identity;
					}
