			}

			protected virtual void OnEnableOverride()
			{
			}

			private void OnDisable()
			{
				if (GLRenderer.Instance != null)
				{
					GLRenderer.Instance.Remove(this);
				}
				OnDisableOverride();
			}

			protected virtual void OnDisableOverride()
			{
			}

			private void OnDestroy()
			{
