				GL.Color((selectedAxis != RuntimeHandleAxis.Y) ? YColor : SelectionColor);
				DrawCircle(transform2, num);
				GL.Color((selectedAxis != RuntimeHandleAxis.Z) ? ZColor : SelectionColor);
				DrawCircle(transform3, num);
				GL.End();
				GL.PopMatrix();
