				m_selectedGameObjects.Clear();
				m_transforms = (from g in gameObjects
					where g.GetComponent<ExposeToEditor>()
					select g.GetComponent<Transform>() into t
					where t.GetType() == typeof(Transform)
					select t).ToArray();
