			public int ToCurveIndex(ref float t)
			{
				int num;
				if (t >= 1f)
				{
					t = 1f;
					num = (m_points.Length - 1) / 3 - 1;
				}
				else
				{
					t = Mathf.Clamp01(t) * (float)CurveCount;
					num = (int)t;
					t -= num;
				}
				return num;
			}

