				command.Blit(aoTexID, num);
				for (int i = 0; i < (int)base.settings.iterations; i++)
				{
					command.SetGlobalVector("_BlurOffsets", new Vector4((float)base.settings.blurAmount / (float)context.screenWidth, 0f, 0f, 0f));
