			private readonly Dictionary<string, UnityEngine.Object> mResourcesCache = new Dictionary<string, UnityEngine.Object>(StringComparer.Ordinal);

			public static ResourceManager pInstance
			{
				get
				{
					bool flag = mInstance == null;
