			internal static string Escape(string aText)
			{
				string text = "";
				for (int i = 0; i < aText.Length; i++)
				{
					char c = aText[i];
					text = c switch
					{
						'\\' => text + "\\\\", 
						'"' => text + "\\\"", 
						'\n' => text + "\\n", 
						'\r' => text + "\\r", 
						'\t' => text + "\\t", 
						'\b' => text + "\\b", 
						'\f' => text + "\\f", 
						_ => text + c, 
					};
				}
				return text;
			}

			public static JSONNode Parse(string aJSON)
			{
				Stack<JSONNode> stack = new Stack<JSONNode>();
				JSONNode jSONNode = null;
				int i = 0;
				string text = "";
				string text2 = "";
				bool flag = false;
				for (; i < aJSON.Length; i++)
				{
					switch (aJSON[i])
					{
					case '{':
						if (flag)
						{
							text += aJSON[i];
							break;
						}
						stack.Push(new JSONClass());
						if (jSONNode != null)
						{
							text2 = text2.Trim();
							if (jSONNode is JSONArray)
							{
								jSONNode.Add(stack.Peek());
							}
							else if (text2 != "")
							{
								jSONNode.Add(text2, stack.Peek());
							}
						}
						text2 = "";
						text = "";
						jSONNode = stack.Peek();
						break;
					case '[':
						if (flag)
						{
							text += aJSON[i];
							break;
						}
						stack.Push(new JSONArray());
						if (jSONNode != null)
						{
							text2 = text2.Trim();
							if (jSONNode is JSONArray)
							{
								jSONNode.Add(stack.Peek());
							}
							else if (text2 != "")
							{
								jSONNode.Add(text2, stack.Peek());
							}
						}
						text2 = "";
						text = "";
						jSONNode = stack.Peek();
						break;
					case ']':
					case '}':
						if (flag)
						{
							text += aJSON[i];
							break;
						}
						if (stack.Count == 0)
						{
							throw new Exception("JSON Parse: Too many closing brackets");
						}
						stack.Pop();
						if (text != "")
						{
							text2 = text2.Trim();
							if (jSONNode is JSONArray)
							{
								jSONNode.Add(text);
							}
							else if (text2 != "")
							{
								jSONNode.Add(text2, text);
							}
						}
						text2 = "";
						text = "";
						if (stack.Count > 0)
						{
							jSONNode = stack.Peek();
						}
						break;
					case ':':
						if (flag)
						{
							text += aJSON[i];
							break;
						}
						text2 = text;
						text = "";
						break;
					case '"':
						flag = !flag;
						break;
					case ',':
						if (flag)
						{
							text += aJSON[i];
							break;
						}
						if (text != "")
						{
							if (jSONNode is JSONArray)
							{
								jSONNode.Add(text);
							}
							else if (text2 != "")
							{
								jSONNode.Add(text2, text);
							}
						}
						text2 = "";
						text = "";
						break;
					case '\t':
					case ' ':
						if (flag)
						{
							text += aJSON[i];
						}
						break;
					case '\\':
						i++;
						if (flag)
						{
							char c = aJSON[i];
							switch (c)
							{
							case 't':
								text += "\t";
								break;
							case 'r':
								text += "\r";
								break;
							case 'n':
								text += "\n";
								break;
							case 'b':
								text += "\b";
								break;
							case 'f':
								text += "\f";
								break;
							case 'u':
							{
								string s = aJSON.Substring(i + 1, 4);
								text += (char)int.Parse(s, NumberStyles.AllowHexSpecifier);
								i += 4;
								break;
							}
							default:
								text += c;
								break;
							}
						}
						break;
					default:
						text += aJSON[i];
						break;
					case '\n':
					case '\r':
						break;
					}
				}
				if (flag)
				{
					throw new Exception("JSON Parse: Quotation marks seems to be messed up.");
				}
				return jSONNode;
			}

