			}
			else
			{
				material.SetFloat(offsetString, 10f * Offset);
			}
			material.SetFloat(fishEyeString, 0.1f * FishEyeDistortion);
		}
		else
		{
			material.DisableKeyword(chromaKeyword);
		}
		if (Distortion)
		{
