	{
		if (Mathf.FloorToInt(UnityEngine.Random.Range(0f, 1.999f)) == 0)
		{
			ballInfos.Add(topBall(originDirection, 0));
			ballInfos.Add(bottomBall(originDirection, 1));
		}
