		}
		if (hipScore <= 10)
		{
			return "";
		}
		if (hipScore <= 20)
		{
			return "";
		}
		if (hipScore <= 30)
		{
			return "";
		}
		if (hipScore <= 40)
		{
			return "";
		}
		if (hipScore <= 50)
		{
			return "";
		}
		if (hipScore <= 60)
		{
			return "";
		}
		if (hipScore <= 70)
		{
			return "";
		}
		if (hipScore <= 80)
		{
			return "";
		}
		if (hipScore <= 90)
		{
			return "";
		}
		return "";
	}

	internal bool checkHipScoreTooHigh(int hipScore, int level)
	{
		if (hipScore > level * 10)
		{
			return true;
		}
		return false;
	}

	internal int getHipScoreBySetting(HipSettingData hipSettingData)
	{
		int num = 0;
		switch (hipSettingData.cubesPerSeconds)
		{
		case 0:
			num = num;
			break;
		case 1:
			num++;
			break;
		case 2:
			num += 2;
			break;
		case 3:
			num += 4;
			break;
		case 4:
			num += 7;
			break;
		case 5:
			num += 12;
			break;
		case 6:
			num += 18;
			break;
		case 7:
			num += 25;
			break;
		case 8:
			num += 30;
			break;
		case 9:
			num += 35;
			break;
		}
		switch (hipSettingData.gateNum)
		{
		case 0:
			num = num;
			break;
		case 1:
			num += 3;
			break;
		case 2:
			num += 8;
			break;
		case 3:
			num += 15;
			break;
		}
		switch (hipSettingData.cubeFlySpeed)
		{
		case 0:
			num = num;
			break;
		case 1:
			num = num;
			break;
		case 2:
			num += 4;
			break;
		case 3:
			num += 8;
			break;
		case 4:
			num += 12;
			break;
		case 5:
			num += 16;
			break;
		case 6:
			num += 20;
			break;
		}
		switch (hipSettingData.comboLevel)
		{
		case 0:
			num = num;
			break;
		case 1:
			num += 3;
			break;
		case 2:
			num += 10;
			break;
		case 3:
			num += 18;
			break;
		case 4:
			num += 30;
			break;
		}
		switch (hipSettingData.wallComboLevel)
		{
		case 0:
			num = num;
			break;
		case 1:
			num += 3;
			break;
		case 2:
			num += 3;
			break;
		}
		return num;
	}
}
