			newHistoryDetailData.getLevel = "";
			UnitySingleton<DataCenter>.Instance.hipSettingDataManager.hipSettingData.level = 5;
			UnitySingleton<DataCenter>.Instance.hipSettingDataManager.SaveHipSettingData(null);
