		public List<int> neibs;

		public RFTriangle(int Id, float Area, UnityEngine.Vector3 Normal, UnityEngine.Vector3 Pos, List<int> Verts)
		{
			id = Id;
			area = Area;
			normal = Normal;
			pos = Pos;
