			Material[] array = ((scr.skinnedMeshRend != null) ? scr.skinnedMeshRend.sharedMaterials : scr.meshRenderer.sharedMaterials);
			for (int i = 0; i < array.Length; i++)
			{
				if (array[i] == scr.materials.innerMaterial)
				{
					return i;
				}
			}
			return -1;
		}

