			int num2 = num * 3;
			float[] array = new float[num2];
			Marshal.Copy(handle, array, 0, num2);
			UnityEngine.Vector3[] array2 = new UnityEngine.Vector3[num];
			for (int i = 0; i < num; i++)
			{
				array2[i] = new UnityEngine.Vector3
				{
					x = array[3 * i],
					y = array[3 * i + 1],
					z = 0f - array[3 * i + 2]
				};
			}
			return array2;
		}

