					material.SetFloat(blurAmountString, (numberOfPasses > 1) ? ((blurAmount * 4f - (float)Mathf.FloorToInt(blurAmount * 4f - 0.001f)) * 0.5f + 0.5f) : (blurAmount * 4f));
