	[Serializable]
	public class OnFrequencyEvent : UnityEvent<float>
	{
	}
	[Serializable]
	public class OnFrequencyChanged
	{
		public OnFrequencyEvent onChange;

		public float minValue;

		public float maxValue = 1f;
