			{
				UnityEngine.Vector3 position = points[i].position;
				UnityEngine.Vector3 position2 = points[i + 1].position;
				UnityEngine.Vector3 vector = position2 - position;
				int num = 0;
				int num2 = lineAtt.lineSegments;
