		base.transform.parent = parentTransform;
		Rigidbody.useGravity = false;
		Rigidbody.isKinematic = true;
		ignoreInteractableTrigger = true;
		inKit = true;
		base.transform.localPosition = originalPos;
		base.transform.localRotation = originalRot;
