	[HideInInspector]
	public bool animating;

	private void Start()
	{
		cross = items[0].gameObject;
	}

	public void OpenKit()
	{
		if (!animating)
		{
			audioScript.PlayOne(open);
			StartCoroutine(Open());
		}
