	public void WatchHintNoVibration(SFXAlertType sfxAlertType)
	{
		AudioClip audioClipForAlterType = getAudioClipForAlterType(sfxAlertType);
		if (audioClipForAlterType != null)
		{
			audioSource.PlayOne(audioClipForAlterType);
		}
