	private void Start()
	{
		if (maggot != null)
		{
			maggot.SetLayer(16);
		}
	}

	private void Update()
	{
		if (isPrecinct)
		{
			float num = bookCover.transform.eulerAngles.x / 176f;
			if (bookCoverEdge.transform.position.x < 4.3f)
			{
				num = 1f - bookCover.transform.eulerAngles.x / 176f;
