		while (!tuneCross.tuneSuccess)
		{
			if (tuneCross.HoldTime > shakeCameraThresholdTime)
			{
				player.screenFX.cameraShakeAmount = 0.04f * (tuneCross.HoldTime - shakeCameraThresholdTime) / (exorcismDuration - shakeCameraThresholdTime);
			}
			else
			{
