		tuneCross.OverrideTuneSuccess = false;
		tuneCross.OverrideHitSuccess = true;
		tuneCross.StartLightTarget(baal.gameObject);
		playerStereoAudio_01.FadeOut(1f);
		playerStereoAudio_02.FadeIn(musicLoopHigh, 1f, 6f, loop: true);
