	private IEnumerator KillMobileMusic(float killTime)
	{
		for (float pitch = 1f; pitch > 0.01f; pitch -= Time.deltaTime / killTime)
		{
			mobileAudio.m_AudioSource.pitch = pitch;
			mobileAudio.m_AudioSource.volume = pitch;
