	private IEnumerator LighterHint()
	{
		float hintTimer = 0f;
		while (!lighter.isOn)
		{
			if (hintTimer > 90f)
			{
				player.HandMenu.OpenStraightToBook(NotebookPages.Q08);
				player.watch.WatchHintWithSFX(5f, SFXAlertType.ALERT);
				break;
			}
			hintTimer += Time.deltaTime;
			yield return Yield.EndOfFrame;
		}
	}

