public class TV_TextureVideo : MonoBehaviour
{
	[SerializeField]
	private int uvAnimationTileX = 4;

	[SerializeField]
	private int uvAnimationTileY = 4;

	[SerializeField]
	private Renderer materialRender;

	[SerializeField]
	private Texture2D screenContentAtlas;

	[SerializeField]
