				position3.y = Mathf.Abs(position3.y) * Mathf.Sign(0f - vector3.y);
				break;
			case AxisList.z:
				position3.z = Mathf.Abs(position3.z) * Mathf.Sign(0f - vector3.z);
				break;
			}
			Col_PortalA.transform.position = GO_PortalA.transform.TransformPoint(position3);
