	[HelpURL("http://updates.rogodigital.com/lipsync-api/class_rogo_digital_1_1_lipsync_1_1_lip_sync.html")]
	public class LipSync : BlendSystemUser
	{
		public delegate void ResetDelegate();

		public enum AnimationTimingMode
		{
			AudioPlayback,
			CustomTimer,
			FixedFrameRate
		}

