		public float CurrentTime
		{
			get
			{
				if (!IsPlaying)
				{
					return 0f;
				}
				if (animationTimingMode == AnimationTimingMode.AudioPlayback)
				{
					return audioSource.time;
				}
				return customTimer;
			}
