		public void Generate()
		{
			if (count < 0)
			{
				count = 0;
			}
			List<GameObject> list = new List<GameObject>();
			for (int i = 0; i < count; i++)
			{
				if (i < base.transform.childCount)
				{
					list.Insert(i, base.transform.GetChild(i).gameObject);
