				}
				list2.Add(image);
			}
			Vector2 rootWH = ((packingOrientation != TexturePackingOrientation.vertical) ? new Vector2(num3, num) : new Vector2(num, num2));
			int outW = (int)rootWH.x;
			int outH = (int)rootWH.y;
