							artefactUnlocked = true;
							EndInteraction(base.AttachedHand);
							CanAttach = false;
						}
					}
					else if (!topLayerUnlocked || !middleLayerUnlocked || !bottomLayerUnlocked)
					{
						switch (currentLevel)
						{
						case LevelEnum.TOP:
						{
							Vector3 localEulerAngles7 = topLayer.transform.localEulerAngles;
							if (reverseDirection)
							{
								if (Mathf.Sign(f) != 1f)
