			hapticActive = true;
			lastHapticFrame = Time.frameCount;
			_ = Controller == Hand.Player.RightHand;
		}

		private IEnumerator DoHapticPulse(ushort durationMicroSec, float power)
		{
			power = Mathf.Clamp01(power);
			hapticActive = true;
			lastHapticFrame = Time.frameCount;
			_ = Controller == Hand.Player.RightHand;
			yield return null;
		}

