	private void exportCharacters()
	{
		string text = "";
		string text2 = "<table>";
		string text3 = "<h1>Character Stories</h1>";
		text2 += "<tr>";
		text2 += "<th>Name</th>";
		text2 += "<th>Race</th>";
		text2 += "<th>Female</th>";
		text2 += "<th>Young</th>";
