			{
				r_bones = getBones(rightHand);
			}
			if (l_bones.Length == 0)
			{
				l_bones = getBones(leftHand);
			}
			r_handRotation_Offset = rightHand.transform.localRotation.eulerAngles;
			l_handRotation_Offset = leftHand.transform.localRotation.eulerAngles;
