	{
		IKSolver iKSolver = ik.GetIKSolver();
		iKSolver.OnPostUpdate = (IKSolver.UpdateDelegate)Delegate.Remove(iKSolver.OnPostUpdate, new IKSolver.UpdateDelegate(OnPostUpdate));
	}

	public override void end()
	{
