	public bool alignToSceneView;

	public Format format;

	private void Start()
	{
		if (height == 0)
		{
			height = width;
		}
		cam = GetComponent<Camera>();
		audioSource = base.gameObject.AddComponent<AudioSource>();
		if (audioSource == null)
		{
			audioSource = base.gameObject.AddComponent<AudioSource>();
		}
