		public static VolumetricFog instance
		{
			get
			{
				if (_fog == null)
				{
					if (Camera.main != null)
					{
						_fog = Camera.main.GetComponent<VolumetricFog>();
					}
					if (_fog == null)
					{
						Camera[] allCameras = Camera.allCameras;
						for (int i = 0; i < allCameras.Length; i++)
						{
							_fog = allCameras[i].GetComponent<VolumetricFog>();
							if (_fog != null)
							{
								break;
							}
						}
					}
				}
				return _fog;
			}
		}

