				if (!fogOfWarTransition.enabled)
				{
					continue;
				}
				float num = Time.time - fogOfWarTransition.startTime - fogOfWarTransition.startDelay;
				if (!(num > 0f))
				{
					continue;
				}
				float num2 = ((fogOfWarTransition.duration <= 0f) ? 1f : (num / fogOfWarTransition.duration));
				if (num2 < 0f)
