				}
				int num3 = (int)((float)fogOfWarTransition.initialAlpha + (float)(fogOfWarTransition.targetAlpha - fogOfWarTransition.initialAlpha) * num2);
				int num4 = fogOfWarTransition.y * width + fogOfWarTransition.x;
