			copyOpaqueMat.SetFloat("_BlendPower", fog.transparencyBlendPower);
			Graphics.Blit(source, destination, copyOpaqueMat, (fog.computeDepth && fog.downsampling == 1) ? 1 : 0);
			RenderTexture.ReleaseTemporary(temporary);
		}

